        public Bitmap compare(Bitmap b, Bitmap template)
        {
            b = ResizeImage(b, template.Width, template.Height);

            Bitmap bout = new Bitmap(template.Width, template.Height);

            for (int x = 0; x < template.Width; x++)
            {
                for (int y = 0; y < template.Height; y++)
                {

                    if (template.GetPixel(x, y) == Color.FromArgb(255, 255, 255))
                    {
                        // bout.SetPixel(x, y, b.GetPixel(x, y));
                        bout.SetPixel(x, y, ColorTranslator.FromHtml("#" + postProcessColor(ColorToHex(b.GetPixel(x, y)))));
                    }
                    
                }
            }

            return bout;
        }

        public Bitmap ResizeImage(Bitmap image, int width, int height)
        {
            var destRect = new Rectangle(0, 0, width, height);
            var destImage = new Bitmap(width, height);

            destImage.SetResolution(image.HorizontalResolution, image.VerticalResolution);

            using (var graphics = Graphics.FromImage(destImage))
            {
                graphics.CompositingMode = CompositingMode.SourceCopy;
                graphics.CompositingQuality = CompositingQuality.HighQuality;
                graphics.InterpolationMode = InterpolationMode.HighQualityBicubic;
                graphics.SmoothingMode = SmoothingMode.HighQuality;
                graphics.PixelOffsetMode = PixelOffsetMode.HighQuality;

                using (var wrapMode = new ImageAttributes())
                {
                    wrapMode.SetWrapMode(WrapMode.TileFlipXY);
                    graphics.DrawImage(image, destRect, 0, 0, image.Width, image.Height, GraphicsUnit.Pixel, wrapMode);
                }
            }

            return destImage;
        }

        string getAvgColor(Bitmap b, Rectangle r)
        {
            b = ResizeImage(b, TEMPLATE.Image.Width, TEMPLATE.Image.Height);

            int i = 0;
            int TR = 0;
            int TG = 0;
            int TB = 0;

            for (int x = 0; x < r.Width; x++)
            {
                for (int y = 0; y < r.Height; y++)
                {
                    Color c = b.GetPixel(r.X + x, r.Y + y);
                    TR += c.R;
                    TG += c.G;
                    TB += c.B;
                    i++;
                }
            }

            Color final = Color.FromArgb(TR / i, TG / i, TB / i);

            return postProcessColor(ColorToHex(final));
        }

        string geMostColor(Bitmap b, Rectangle r)
        {
            b = ResizeImage(b, TEMPLATE.Image.Width, TEMPLATE.Image.Height);

            Dictionary<string, int> colorCount = new Dictionary<string, int>();

            for (int x = 0; x < r.Width; x++)
            {
                for (int y = 0; y < r.Height; y++)
                {
                    string color = ColorToHex(b.GetPixel(r.X + x, r.Y + y));

                    if (colorCount.ContainsKey(color))
                    {
                        colorCount[color] += 1;
                    }
                    else
                    {
                        colorCount.Add(color, 1);
                    }
                }
            }

            int max = 0;
            string maxC = "";
            foreach (KeyValuePair<string, int> entry in colorCount)
            {
                if (entry.Value > max)
                {
                    max = entry.Value;
                    maxC = entry.Key;
                }
            }

            return postProcessColor(maxC);
        }

        string geBrightestColor(Bitmap b, Rectangle r)
        {
            b = ResizeImage(b, TEMPLATE.Image.Width, TEMPLATE.Image.Height);

            Dictionary<string, int> colorCount = new Dictionary<string, int>();

            int max = 0;
            string maxC = "";

            for (int x = 0; x < r.Width; x++)
            {
                for (int y = 0; y < r.Height; y++)
                {
                    Color c = b.GetPixel(r.X + x, r.Y + y);
                    int brightness = c.R * c.G * c.B;

                    if (brightness > max)
                    {
                        max = brightness;
                        maxC = ColorToHex(c);
                    }

                }
            }


            return postProcessColor(maxC);
        }

        void calcAllColors()
        {
            foreach (KeyValuePair<string, Rectangle> entry in keysRects)
            {
                if (avergeToolStripMenuItem.Checked)
                {
                    keysColors[entry.Key] = getAvgColor((Bitmap)pictureBox1.Image, entry.Value);
                }
                else if (mostOftenToolStripMenuItem.Checked)
                {
                    keysColors[entry.Key] = geMostColor((Bitmap)pictureBox1.Image, entry.Value);
                }
                else if (brightestToolStripMenuItem.Checked)
                {
                    keysColors[entry.Key] = geBrightestColor((Bitmap)pictureBox1.Image, entry.Value);
                }

            }
        }

        string postProcessColor(string s)
        {
            Color c = ColorTranslator.FromHtml("#" + s);
            Color output = c;

            if (chkMaxBrightness.Checked)
            {
                int maxVal = c.R;
                if (c.G > maxVal) maxVal = c.G;
                if (c.B > maxVal) maxVal = c.B;

                int MBR = 255 - maxVal;

                output = Color.FromArgb(Clamp(c.R + MBR, 0, 255), Clamp(c.G + MBR, 0, 255), Clamp(c.B + MBR, 0, 255));
            }
            else if (brightness != 0)
            {
                output = Color.FromArgb(Clamp(c.R + brightness, 0, 255), Clamp(c.G + brightness, 0, 255), Clamp(c.B + brightness, 0, 255));
            }


            if (invertBWToolStripMenuItem.Checked)
            {
                if ((c.R > 255 - invertThreshold && c.G > 255 - invertThreshold && c.B > 255 - invertThreshold) || (c.R < invertThreshold && c.G < invertThreshold && c.B < invertThreshold))
                {
                    //color is black or white
                    output = Color.FromArgb(Math.Abs(c.R - 255), Math.Abs(c.G - 255), Math.Abs(c.B - 255));
                    // output = Color.FromArgb(Math.Abs(255-c.R ), Math.Abs(255-c.G), Math.Abs(255-c.B));


                }
            }

            return ColorToHex(output);
        }

        void displayRealColors()
        {
            Bitmap output = new Bitmap(TEMPLATE.Image.Width, TEMPLATE.Image.Height);

            foreach (var entry in keysRects)
            {
                Rectangle r = entry.Value;
                string color = keysColors[entry.Key];

                Bitmap template = (Bitmap)TEMPLATE.Image;

                for (int x = 0; x < r.Width; x++)
                {
                    for (int y = 0; y < r.Height; y++)
                    {
                        output.SetPixel(r.X + x, r.Y + y, ColorTranslator.FromHtml("#" + color));
                    }
                }

            }
            pictureBox2.Image = output;

        }

        void writeEntries(string path)
