            //entry: 
            //<lightBackground color="#COLOR" key="KEY"/>

            if (fullProfileToolStripMenuItem.Checked)
            {
                writefullprofile(path);

            }
            else
            {
                using (System.IO.StreamWriter file = new System.IO.StreamWriter(path))
                {
                    foreach (KeyValuePair<string, string> entry in keysColors)
                    {
                        string s = @"<lightBackground color=""#" + entry.Value + @""" key=""" + entry.Key + @"""/>";
                        file.WriteLine(s);

                    }
                }


            }



        }

