        #region Menu Controls

        private void mnuOpen_Click(object sender, EventArgs e)
        {
            using (OpenFileDialog dlg = new OpenFileDialog())
            {
                dlg.Title = "Open Image";
                dlg.Filter = "Image Files (*.bmp, *.jpg, *.png)|*.bmp;*.jpg;*.png";

                if (dlg.ShowDialog() == DialogResult.OK)
                {

                    pictureBox1.Image = new Bitmap(dlg.FileName);
                }
            }
        }

        private void mnuSave_Click(object sender, EventArgs e)
        {

            SaveFileDialog saveFileDialog1 = new SaveFileDialog();

            if (fullProfileToolStripMenuItem.Checked)
            {
                saveFileDialog1.Filter = "Profile Files (*.prf)|*.prf|All files (*.*)|*.*";
            }
            else
            {
                saveFileDialog1.Filter = "txt files (*.txt)|*.txt|All files (*.*)|*.*";
            }

            saveFileDialog1.FilterIndex = 1;
            saveFileDialog1.RestoreDirectory = true;

            if (saveFileDialog1.ShowDialog() == DialogResult.OK)
            {
                writeEntries(saveFileDialog1.FileName);
            }
        }

        private void mostOftenToolStripMenuItem_CheckedChanged(object sender, EventArgs e)
        {
            if (mostOftenToolStripMenuItem.Checked)
            {
                avergeToolStripMenuItem.Checked = false;
                brightestToolStripMenuItem.Checked = false;
            }
        }

        private void avergeToolStripMenuItem_CheckedChanged(object sender, EventArgs e)
        {
            if (avergeToolStripMenuItem.Checked)
            {
                mostOftenToolStripMenuItem.Checked = false;
                brightestToolStripMenuItem.Checked = false;
            }
        }

        private void brightestToolStripMenuItem_CheckedChanged(object sender, EventArgs e)
        {
            if (brightestToolStripMenuItem.Checked)
            {
                mostOftenToolStripMenuItem.Checked = false;
                avergeToolStripMenuItem.Checked = false;
            }
        }

        private void fullProfileToolStripMenuItem_CheckedChanged(object sender, EventArgs e)
        {
            if (fullProfileToolStripMenuItem.Checked)
            {
                lightingOnlyToolStripMenuItem.Checked = false;
            }
        }

        private void lightingOnlyToolStripMenuItem_CheckedChanged(object sender, EventArgs e)
        {
            if (lightingOnlyToolStripMenuItem.Checked)
            {
                fullProfileToolStripMenuItem.Checked = false;
            }
        }

        private void startToolStripMenuItem_Click(object sender, EventArgs e)
        {
            if (!realColorToolStripMenuItem.Checked)
                pictureBox2.Image = compare((Bitmap)pictureBox1.Image, (Bitmap)TEMPLATE.Image);
            calcAllColors();
            if (realColorToolStripMenuItem.Checked)
                displayRealColors();

            brightness = 0;
            txtBrightness.Clear();
        }

